import axios from "axios";
import { useState } from "react"

function Entry(){
    const [date, setDate] = useState("");
    const [time, setTime] = useState("");
    const [vchef, setVChef] = useState("");
    const [nchef, setNChef] = useState("");
    // const [vitem,setVitem]=useState([])
    // const [nvitem,setNvitem]=useState([])
    // const [vstate,setVstate]=useState(false)
    // const [nvstate,setNVstate]=useState(false)
    // const [vdummy,setVDummy]=useState("")
    // const [ndummy,setNDummy]=useState("")
    const handleSubmit = () => {
        const newList = {
            date,
            time,
            vchef,
            nchef
        };
        axios.post('http://localhost:3500/api/lists',
        // alert("done"),
         {newList}     //changed
         )
        .then(response => {
            if (response.data.status === "ok") {
                alert("Status = "+response.data.status);
            }
        })
        .catch(err => {
            console.log(err);
        });

        setVChef("");
        setNChef("");
        setDate("");
        setTime("");
    }

    return (
        <div className="fill">
            <form onSubmit={handleSubmit}>
            <input type='date' name='date' required value={date} onChange={(e) => setDate(e.target.value)} />
            <br></br>
            <input list='time' name='time' required value={time} placeholder='Enter the timing' onChange={(e) => setTime(e.target.value)} />
            <datalist id='time' >
                <option value={"11.00 am to 1.00 am"} />
                <option value={"1.00 pm to 3.00 pm"} />
                <option value={"6.00 pm to 9.00 pm"} />
            </datalist>
            <br></br>
            <input type="text" value={vchef} required placeholder="Enter Veg Chef Name" onChange={(e) => setVChef(e.target.value)} />
            <br></br>
            <input type="text" value={nchef} required placeholder="Enter Non Veg Chef Name" onChange={(e) => setNChef(e.target.value)} />
            <br></br>
            <input type="submit" />
            </form>
            {/* <button onClick={} >Submit</button> */}
        </div>
    )
}

export default Entry;
